SET FOREIGN_KEY_CHECKS=0;DROP TABLE IF EXISTS `t_appendix`;CREATE TABLE `t_appendix` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `articleId` int(11) DEFAULT NULL,  `fileId` int(11) DEFAULT NULL,  `sortId` int(11) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;DROP TABLE IF EXISTS `t_article`;CREATE TABLE `t_article` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `pubUnit` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL,  `pubDate` timestamp NULL DEFAULT NULL,  `docId` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL,  `sectionId` int(11) DEFAULT NULL,  `title` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,  `hits` int(11) DEFAULT NULL,  `checked` int(11) DEFAULT NULL,  `userId` int(11) DEFAULT NULL,  `onHomePage` int(11) DEFAULT NULL,  `content` mediumtext COLLATE utf8_unicode_ci,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;DROP TABLE IF EXISTS `t_dep`;CREATE TABLE `t_dep` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `depName` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,  `parentId` int(11) DEFAULT NULL,  `depType` int(11) DEFAULT NULL,  `depDescription` text COLLATE utf8_unicode_ci,  `contact` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;DROP TABLE IF EXISTS `t_dir`;CREATE TABLE `t_dir` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `dirName` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,  `parentDir` int(11) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;DROP TABLE IF EXISTS `t_file`;CREATE TABLE `t_file` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `fileName` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,  `fileDiskName` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL,  `fileSize` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,  `fileType` varchar(10) COLLATE utf8_unicode_ci DEFAULT NULL,  `url` varchar(200) COLLATE utf8_unicode_ci DEFAULT NULL,  `userId` int(11) DEFAULT NULL,  `uploadIp` varchar(15) COLLATE utf8_unicode_ci DEFAULT NULL,  `uploadTime` timestamp NULL DEFAULT NULL,  `dirId` int(11) DEFAULT NULL,  `fileRight` int(11) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;DROP TABLE IF EXISTS `t_log`;CREATE TABLE `t_log` (  `id` int(11) NOT NULL,  `runner` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL,  `event` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,  `run_time` timestamp NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP,  `ip` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,  `info` varchar(300) COLLATE utf8_unicode_ci DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;DROP TABLE IF EXISTS `t_person`;CREATE TABLE `t_person` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `userId` int(11) DEFAULT NULL,  `personName` varchar(30) COLLATE utf8_unicode_ci DEFAULT NULL,  `personSex` int(11) DEFAULT NULL,  `personTel` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL,  `personMobile` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,  `depId` int(11) DEFAULT NULL,  `proxyRole` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL,  `roleId` int(11) DEFAULT NULL,  `personEmail` varchar(150) COLLATE utf8_unicode_ci DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;DROP TABLE IF EXISTS `t_role`;CREATE TABLE `t_role` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `roleName` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,  `roleDescription` text COLLATE utf8_unicode_ci,  `validity` timestamp NULL DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;DROP TABLE IF EXISTS `t_section`;CREATE TABLE `t_section` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `sectionName` varchar(100) COLLATE utf8_unicode_ci DEFAULT NULL,  `parentSection` int(11) DEFAULT NULL,  `sectionType` int(11) DEFAULT NULL,  `sortId` int(11) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;DROP TABLE IF EXISTS `t_user`;CREATE TABLE `t_user` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `userCode` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,  `userSId` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,  `password` varchar(32) COLLATE utf8_unicode_ci DEFAULT NULL,  `lastLoginIp` varchar(15) COLLATE utf8_unicode_ci DEFAULT NULL,  `lastLoginTime` timestamp NULL DEFAULT NULL,  `validity` timestamp NULL DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;